class Solution:
    def numSquares(self, n: int) -> int:
        import math
        # Generate list of perfect squares less than or equal to n
        squares = []
        i = 1
        while i * i <= n:
            squares.append(i * i)
            i += 1

        # DP array to store minimum number of perfect squares sum to each number up to n
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Fill dp array
        for num in range(1, n + 1):
            for square in squares:
                if num < square:
                    break
                dp[num] = min(dp[num], dp[num - square] + 1)

        return dp[n]

