    def threeSum(self, nums: List[int]) -> List[List[int]]:
        nums.sort()  # Sort the array to simplify handling duplicates and to use two pointer technique.
        n = len(nums)
        triplets = []

        for i in range(n):
            # Avoid duplicate values for the first element to ensure unique triplets.
            if i > 0 and nums[i] == nums[i - 1]:
                continue

            target = -nums[i]
            left, right = i + 1, n - 1

            while left < right:
                current_sum = nums[left] + nums[right]

                if current_sum == target:
                    triplets.append([nums[i], nums[left], nums[right]])
                    left += 1
                    right -= 1

                    # Skip duplicate values for the second element
                    while left < right and nums[left] == nums[left - 1]:
                        left += 1

                    # Skip duplicate values for the third element
                    while left < right and nums[right] == nums[right + 1]:
                        right -= 1

                elif current_sum < target:
                    left += 1  # If current sum is less than target, move left to increase sum.
                else:
                    right -= 1  # If current sum is more than target, move right to decrease sum.

        return triplets
