class Solution:
    def minDistance(self, word1: str, word2: str) -> int:
        # Lengths of the words to be used in dynamic programming table
        m, n = len(word1), len(word2)

        # Create a 2D dp array initialized with 0s
        # dp[i][j] will hold the number of operations needed to convert word1[:i] to word2[:j]
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        # Base case: converting word1 to an empty string (word2) requires i deletions
        for i in range(1, m + 1):
            dp[i][0] = i
        # Base case: converting empty string (word1) to word2 requires j insertions
        for j in range(1, n + 1):
            dp[0][j] = j

        # Fill the dp table using the recursive relation
        for i in range(1, m + 1):
            for j in range(1, n + 1):
                if word1[i - 1] == word2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1]  # no operation needed
                else:
                    dp[i][j] = min(
                        dp[i - 1][j] + 1,    # delete word1[i-1]
                        dp[i][j - 1] + 1,    # insert word2[j-1]
                        dp[i - 1][j - 1] + 1 # replace word1[i-1] with word2[j-1]
                    )

        # The last cell of the dp table contains the answer
        return dp[m][n]
