class Solution:
    def maxVowels(self, s: str, k: int) -> int:
        # Set of vowel letters
        vowels = set('aeiou')

        # Initialize variables to store maximum vowels in a substring
        max_vowels = 0
        current_vowels = 0

        # Build the initial window and count vowels within the first k characters
        for i in range(k):
            if s[i] in vowels:
                current_vowels += 1

        # This will hold the maximum count of vowels observed so far
        max_vowels = current_vowels

        # Slide the window across the string from position k to the end
        for i in range(k, len(s)):
            # Add a vowel if the new character entering the window is a vowel
            if s[i] in vowels:
                current_vowels += 1
            # Remove a vowel if the character leaving the window is a vowel
            if s[i - k] in vowels:
                current_vowels -= 1

            # Update the maximum number of vowels found if the current count is greater
            max_vowels = max(max_vowels, current_vowels)

        # Return the maximum number of vowels found in any substring of length k
        return max_vowels
