from typing import List

class Solution:
    def maxSubarraySumCircular(self, nums: List[int]) -> int:
        def kadane(array: List[int]) -> int:
            # Traditional Kadane's algorithm for maximum subarray sum
            max_current = array[0]
            max_global = array[0]
            for num in array[1:]:
                max_current = max(num, max_current + num)
                max_global = max(max_global, max_current)
            return max_global

        # Total sum of the array
        total_sum = sum(nums)

        # Find max subarray sum using Kadane's algorithm
        max_kadane = kadane(nums)

        # Find minimum subarray sum using reverse Kadane's algorithm
        # Invert the sign of array elements and apply Kadane's to find min subarray sum
        inverted_max_kadane = kadane([-num for num in nums])

        max_circular = total_sum + inverted_max_kadane

        # If max_circular is 0, it means all numbers are negative, which leads to all numbers being considered for the circular sum and cancellation to 0, we should return max_kadane instead.
        if max_circular == 0:
            return max_kadane
        else:
            return max(max_kadane, max_circular)
