class Solution:
    def reverseVowels(self, s: str) -> str:
        # Algorithm Choice: Two-pointer technique
        # Step-by-Step Approach:
        # 1. Define a set containing all vowel characters (consider both cases).
        vowels = set('aeiouAEIOU')

        # 2. Convert the string to a list to make modifications easier.
        s_list = list(s)

        # 3. Initialize two pointers, left and right, to locate vowels from both ends of the list.
        left, right = 0, len(s_list) - 1

        # 4. Sequentially adjust the pointers until they meet.
        while left < right:
            # Move the left pointer until it points to a vowel.
            while left < right and s_list[left] not in vowels:
                left += 1

            # Move the right pointer until it points to a vowel.
            while left < right and s_list[right] not in vowels:
                right -= 1

            # Swap the vowels at the left and right pointers.
            if left < right:
                s_list[left], s_list[right] = s_list[right], s_list[left]
                left += 1
                right -= 1

        # 5. Join the list back into a string and return it as the result.
        return ''.join(s_list)
