    def summaryRanges(self, nums: List[int]) -> List[str]:
        if not nums:
            return []

        ranges = []
        start = nums[0]  # Start of the current range

        for i in range(1, len(nums)):
            # If the current number is not the consecutive number of the previous
            if nums[i] != nums[i - 1] + 1:
                end = nums[i - 1]  # Last number of the current consecutive range

                # If the start equals the end, it's a single number range
                if start == end:
                    ranges.append(f"{start}")
                else:
                    ranges.append(f"{start}->{end}")

                # Update start for the next range
                start = nums[i]

        # Handle the last range after the loop
        end = nums[-1]
        if start == end:
            ranges.append(f"{start}")
        else:
            ranges.append(f"{start}->{end}")

        return ranges
