class Solution:
    def maxScoreWords(self, words: List[str], letters: List[str], score: List[int]) -> int:
        from collections import Counter

        letter_count = Counter(letters)

        def get_word_score(word):
            return sum(score[ord(c) - ord('a')] for c in word)

        def can_form_word(word, available):
            word_count = Counter(word)
            for char, count in word_count.items():
                if available[char] < count:
                    return False
            return True

        def backtrack(index, available):
            if index == len(words):
                return 0

            max_score = backtrack(index + 1, available)

            word = words[index]
            if can_form_word(word, available):
                word_count = Counter(word)
                for char, count in word_count.items():
                    available[char] -= count

                current_score = get_word_score(word) + backtrack(index + 1, available)
                max_score = max(max_score, current_score)

                for char, count in word_count.items():
                    available[char] += count

            return max_score

        return backtrack(0, letter_count)
