from typing import List

class Solution:
    def minDays(self, bloomDay: List[int], m: int, k: int) -> int:
        n = len(bloomDay)

        if m * k > n:
            return -1

        def canMakeBouquets(day):
            bouquets = 0
            consecutive = 0

            for bloom in bloomDay:
                if bloom <= day:
                    consecutive += 1
                    if consecutive == k:
                        bouquets += 1
                        consecutive = 0
                else:
                    consecutive = 0

            return bouquets >= m

        left = min(bloomDay)
        right = max(bloomDay)
        result = -1

        while left <= right:
            mid = (left + right) // 2

            if canMakeBouquets(mid):
                result = mid
                right = mid - 1
            else:
                left = mid + 1

        return result

