from typing import List

class Solution:
    def minimumTime(self, time: List[int], totalTrips: int) -> int:
        def canComplete(t):
            trips = 0
            for bus_time in time:
                trips += t // bus_time
                if trips >= totalTrips:
                    return True
            return False

        left = 1
        right = min(time) * totalTrips

        while left < right:
            mid = (left + right) // 2
            if canComplete(mid):
                right = mid
            else:
                left = mid + 1

        return left
