class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        count = {'a': 0, 'b': 0, 'c': 0}
        left = 0
        result = 0

        for right in range(n):
            count[s[right]] += 1

            while count['a'] > 0 and count['b'] > 0 and count['c'] > 0:
                result += n - right
                count[s[left]] -= 1
                left += 1

        return result

