class Solution:
    def maxScoreWords(self, words: List[str], letters: List[str], score: List[int]) -> int:
        from collections import Counter
        n = len(words)
        avail = Counter(letters)
        max_score = 0

        word_counts = [Counter(word) for word in words]
        word_scores = []
        for word in words:
            s = 0
            for c in word:
                s += score[ord(c) - ord('a')]
            word_scores.append(s)

        def backtrack(i, curr_avail, curr_score):
            nonlocal max_score
            if i == n:
                if curr_score > max_score:
                    max_score = curr_score
                return

            backtrack(i+1, curr_avail, curr_score)

            word_count = word_counts[i]
            valid = True
            for char in word_count:
                if word_count[char] > curr_avail[char]:
                    valid = False
                    break

            if valid:
                new_avail = curr_avail.copy()
                for char in word_count:
                    new_avail[char] -= word_count[char]
                backtrack(i+1, new_avail, curr_score + word_scores[i])

        backtrack(0, avail, 0)
        return max_score

