class Solution:
    def removeStones(self, stones: List[List[int]]) -> int:
        parent = {}
        rank = {}

        def find(x):
            if parent[x] != x:
                parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            rx = find(x)
            ry = find(y)
            if rx == ry:
                return
            if rank[rx] < rank[ry]:
                parent[rx] = ry
            elif rank[rx] > rank[ry]:
                parent[ry] = rx
            else:
                parent[ry] = rx
                rank[rx] += 1

        # For each stone, union its row and column
        for x, y in stones:
            row_key = f"r{x}"
            col_key = f"c{y}"

            if row_key not in parent:
                parent[row_key] = row_key
                rank[row_key] = 0

            if col_key not in parent:
                parent[col_key] = col_key
                rank[col_key] = 0

            union(row_key, col_key)

        # Count number of connected components
        components = set()
        for key in parent:
            components.add(find(key))

        return len(stones) - len(components)
