    def tallestBillboard(self, rods: List[int]) -> int:
        n = len(rods)
        total = sum(rods)

        dp = [-1] * (total + 1)
        dp[0] = 0

        for rod in rods:
            curr_dp = dp[:]
            for diff in range(total + 1):
                if curr_dp[diff] == -1:
                    continue

                # Add to taller side
                new_diff = diff + rod
                dp[new_diff] = max(dp[new_diff], curr_dp[diff] + rod)

                # Add to shorter side
                new_diff = abs(diff - rod)
                dp[new_diff] = max(dp[new_diff], curr_dp[diff] + rod)

        return dp[0] // 2 if dp[0] > 0 else 0
