class Solution:
    def backspaceCompare(self, s: str, t: str) -> bool:
        i = len(s) - 1
        j = len(t) - 1

        while i >= 0 or j >= 0:
            skip_s = 0
            while i >= 0:
                if s[i] == '#':
                    skip_s += 1
                    i -= 1
                elif skip_s > 0:
                    skip_s -= 1
                    i -= 1
                else:
                    break

            skip_t = 0
            while j >= 0:
                if t[j] == '#':
                    skip_t += 1
                    j -= 1
                elif skip_t > 0:
                    skip_t -= 1
                    j -= 1
                else:
                    break

            # Now i and j point to the next non-backspaced char, or are < 0.
            # If one string has a valid character and the other doesn't, they are not equal.
            if (i >= 0) != (j >= 0):
                return False

            # If both strings have a valid character, they must be the same.
            if i >= 0 and j >= 0 and s[i] != t[j]:
                return False

            # Move to the characters before the current ones
            i -= 1
            j -= 1

        return True
