class Solution:
    def countVowelStrings(self, n: int) -> int:
        # dp[j] represents the number of sorted strings of a given length
        # that end with the j-th vowel (0-indexed: a, e, i, o, u).

        # Base case for n=1: ["a", "e", "i", "o", "u"]
        # There is one string of length 1 ending with each vowel.
        dp = [1, 1, 1, 1, 1]

        # We iterate n-1 times to build the solution for length n from length 1.
        # Each iteration corresponds to increasing the string length by 1.
        for _ in range(n - 1):
            # The transition logic updates the counts for the next length.
            # A sorted string of length i ending with vowel v_j can be formed
            # by appending v_j to any sorted string of length i-1 ending
            # with a vowel v_k where k <= j.
            # This is equivalent to calculating the prefix sum of the dp array
            # from the previous length.
            # This inner loop calculates the prefix sums in-place.
            for j in range(1, 5):
                dp[j] += dp[j - 1]

        # The total count is the sum of strings of length n ending in any vowel.
        return sum(dp)
