from typing import List

class Solution:
    def smallestDistancePair(self, nums: List[int], k: int) -> int:
        nums.sort()
        n = len(nums)

        low = 0
        high = nums[-1] - nums[0]

        while low < high:
            mid = low + (high - low) // 2

            count = 0
            left = 0
            for right in range(n):
                while nums[right] - nums[left] > mid:
                    left += 1
                count += right - left

            if count >= k:
                high = mid
            else:
                low = mid + 1

        return low

