import bisect
from typing import List

class Solution:
    def longestObstacleCourseAtEachPosition(self, obstacles: List[int]) -> List[int]:
        tails = []
        ans = []
        for obs in obstacles:
            k = bisect.bisect_right(tails, obs)
            ans.append(k + 1)
            if k == len(tails):
                tails.append(obs)
            else:
                tails[k] = obs
        return ans

