import math
from typing import List

class Solution:
    def minimumTime(self, time: List[int], totalTrips: int) -> int:
        low = 1
        high = min(time) * totalTrips

        ans = high

        while low <= high:
            mid = low + (high - low) // 2
            if mid == 0:
                low = 1
                continue

            completed_trips = 0
            for t in time:
                completed_trips += mid // t

            if completed_trips >= totalTrips:
                ans = mid
                high = mid - 1
            else:
                low = mid + 1

        return ans

