class Solution:
    def myAtoi(self, s: str) -> int:
        INT_MAX = 2**31 - 1
        INT_MIN = -2**31

        i = 0
        n = len(s)

        while i < n and s[i] == ' ':
            i += 1

        sign = 1
        if i < n:
            if s[i] == '+':
                i += 1
            elif s[i] == '-':
                sign = -1
                i += 1

        result = 0
        while i < n and s[i].isdigit():
            digit = int(s[i])

            if sign == 1:
                if result > INT_MAX // 10 or (result == INT_MAX // 10 and digit > 7):
                    return INT_MAX
            else:
                if result > -INT_MIN // 10 or (result == -INT_MIN // 10 and digit > 8):
                    return INT_MIN

            result = result * 10 + digit
            i += 1

        return sign * result

