class Solution:
    def kWeakestRows(self, mat: list[list[int]], k: int) -> list[int]:
        row_strengths = []
        for i, row in enumerate(mat):
            strength = sum(row)
            row_strengths.append((strength, i))

        row_strengths.sort()

        result = [index for strength, index in row_strengths[:k]]

        return result

