class Solution:
    def judgePoint24(self, cards: List[int]) -> bool:
        EPS = 1e-6
        TARGET = 24.0

        def backtrack(nums: List[float]) -> bool:
            if len(nums) == 1:
                return abs(nums[0] - TARGET) < EPS

            n = len(nums)
            for i in range(n):
                for j in range(i + 1, n):
                    a, b = nums[i], nums[j]
                    rest = [nums[k] for k in range(n) if k != i and k != j]

                    results = [
                        a + b,
                        a - b,
                        b - a,
                        a * b
                    ]

                    if abs(b) > EPS:
                        results.append(a / b)
                    if abs(a) > EPS:
                        results.append(b / a)

                    for r in results:
                        if backtrack(rest + [r]):
                            return True
            return False

        nums = [float(x) for x in cards]
        return backtrack(nums)

