from typing import List
from collections import defaultdict

class Solution:
    def numberOfArithmeticSlices(self, nums: List[int]) -> int:
        n = len(nums)
        if n < 3:
            return 0
        dp = [defaultdict(int) for _ in range(n)]
        res = 0
        for j in range(n):
            for i in range(j):
                diff = nums[j] - nums[i]
                cnt_i = dp[i][diff]
                res += cnt_i
                dp[j][diff] += cnt_i + 1
        return res

