from typing import List

class Solution:
    def countRangeSum(self, nums: List[int], lower: int, upper: int) -> int:
        n = len(nums)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i + 1] = prefix[i] + nums[i]

        def sort_and_count(lo: int, hi: int) -> int:
            if hi - lo <= 1:
                return 0
            mid = (lo + hi) // 2
            count = sort_and_count(lo, mid) + sort_and_count(mid, hi)

            j_lower = j_upper = mid
            for i in range(lo, mid):
                while j_lower < hi and prefix[j_lower] - prefix[i] < lower:
                    j_lower += 1
                while j_upper < hi and prefix[j_upper] - prefix[i] <= upper:
                    j_upper += 1
                count += j_upper - j_lower

            temp = []
            i, j = lo, mid
            while i < mid and j < hi:
                if prefix[i] <= prefix[j]:
                    temp.append(prefix[i])
                    i += 1
                else:
                    temp.append(prefix[j])
                    j += 1
            while i < mid:
                temp.append(prefix[i])
                i += 1
            while j < hi:
                temp.append(prefix[j])
                j += 1
            prefix[lo:hi] = temp
            return count

        return sort_and_count(0, n + 1)

