from typing import List

class Solution:
    def maxDotProduct(self, nums1: List[int], nums2: List[int]) -> int:
        n, m = len(nums1), len(nums2)
        neg_inf = -10**15
        dp = [[neg_inf] * (m + 1) for _ in range(n + 1)]

        for i in range(1, n + 1):
