from typing import List

class Solution:
    def maxScoreWords(self, words: List[str], letters: List[str], score: List[int]) -> int:
        letter_counts = [0] * 26
        for ch in letters:
            letter_counts[ord(ch) - 97] += 1

        filtered_words = []
        word_letter_counts = []
        word_scores = []

        for w in words:
            counts = [0] * 26
            s = 0
            for ch in w:
                idx = ord(ch) - 97
                counts[idx] += 1
                s += score[idx]
            possible = True
            for i in range(26):
                if counts[i] > letter_counts[i]:
                    possible = False
                    break
            if possible:
                filtered_words.append(w)
                word_letter_counts.append(counts)
                word_scores.append(s)

        m = len(filtered_words)

        def dfs(i: int) -> int:
            if i == m:
                return 0

            best = dfs(i + 1)

            can_take = True
            wl = word_letter_counts[i]
            for k in range(26):
                if wl[k] > 0 and letter_counts[k] < wl[k]:
                    can_take = False
                    break

            if can_take:
                for k in range(26):
                    if wl[k] > 0:
                        letter_counts[k] -= wl[k]
                val = word_scores[i] + dfs(i + 1)
                if val > best:
                    best = val
                for k in range(26):
                    if wl[k] > 0:
                        letter_counts[k] += wl[k]

            return best

        return dfs(0)

