from typing import List
import heapq

class Solution:
    def maxScore(self, nums1: List[int], nums2: List[int], k: int) -> int:
        pairs = sorted(zip(nums2, nums1), reverse=True)
        heap = []
        curr_sum = 0
        ans = 0
        for b, a in pairs:
            heapq.heappush(heap, a)
            curr_sum += a
            if len(heap) > k:
                curr_sum -= heapq.heappop(heap)
            if len(heap) == k:
                ans = max(ans, curr_sum * b)
        return ans

