from typing import List

class Solution:
    def distinctNames(self, ideas: List[str]) -> int:
        groups = [set() for _ in range(26)]
        for w in ideas:
            first = ord(w[0]) - ord('a')
            suffix = w[1:]
            groups[first].add(suffix)

        res = 0
        for i in range(26):
            for j in range(i + 1, 26):
                if not groups[i] or not groups[j]:
                    continue
                # count common suffixes
                if len(groups[i]) < len(groups[j]):
                    smaller, larger = groups[i], groups[j]
                else:
                    smaller, larger = groups[j], groups[i]

                common = 0
                for s in smaller:
                    if s in larger:
                        common += 1

                unique_i = len(groups[i]) - common
                unique_j = len(groups[j]) - common
                if unique_i > 0 and unique_j > 0:
                    res += unique_i * unique_j * 2

        return res

