from typing import List

class Solution:
    def palindromePairs(self, words: List[str]) -> List[List[int]]:
        def is_palindrome(s: str, l: int, r: int) -> bool:
            while l < r:
                if s[l] != s[r]:
                    return False
                l += 1
                r -= 1
            return True

        word_to_index = {w: i for i, w in enumerate(words)}
        res = []

        for i, word in enumerate(words):
            m = len(word)
            for k in range(m + 1):
                if is_palindrome(word, 0, k - 1):
                    right = word[k:]
                    j = word_to_index.get(right[::-1])
                    if j is not None and j != i:
                        res.append([j, i])
                if k != m and is_palindrome(word, k, m - 1):
                    left = word[:k]
                    j = word_to_index.get(left[::-1])
                    if j is not None and j != i:
                        res.append([i, j])

        return res

