    def parseBoolExpr(self, expression: str) -> bool:
        stack = []
        for ch in expression:
            if ch == ',':
                continue
            if ch != ')':
                stack.append(ch)
            else:
                has_true = False
                has_false = False
                while stack and stack[-1] != '(':
                    val = stack.pop()
                    if val == 't':
                        has_true = True
                    elif val == 'f':
                        has_false = True
                stack.pop()  # remove '('
