class Solution:
    def racecar(self, target: int) -> int:
        from functools import lru_cache

        @lru_cache(None)
        def dp(t):
            if t == 0:
                return 0
            # n such that (1<<n)-1 >= t
            n = t.bit_length()
            if (1 << n) - 1 == t:
                return n

            # Option 1: overshoot then come back
            res = n + 1 + dp((1 << n) - 1 - t)

            # Option 2: undershoot, reverse before reaching or at n-1 steps
            for m in range(n - 1):
                dist_forward = (1 << (n - 1)) - 1
                dist_back = (1 << m) - 1
                remaining = t - dist_forward + dist_back
                if remaining >= 0:
                    res = min(res, (n - 1) + 1 + m + 1 + dp(remaining))

            return res

        return dp(target)

