from typing import List

class Solution:
    def smallestSufficientTeam(self, req_skills: List[str], people: List[List[str]]) -> List[int]:
        m = len(req_skills)
        skill_index = {skill: i for i, skill in enumerate(req_skills)}
        people_masks = []
        for skills in people:
            mask = 0
            for s in skills:
                if s in skill_index:
                    mask |= 1 << skill_index[s]
            people_masks.append(mask)
        dp = {0: []}
        full_mask = (1 << m) - 1
        for i, p_mask in enumerate(people_masks):
            if p_mask == 0:
                continue
            new_dp = dict(dp)
            for mask, team in dp.items():
                comb = mask | p_mask
                if comb == mask:
                    continue
                if comb not in new_dp or len(new_dp[comb]) > len(team) + 1:
                    new_dp[comb] = team + [i]
            dp = new_dp
        return dp[full_mask]

