class Solution:
    def tallestBillboard(self, rods: List[int]) -> int:
        dp = {0: 0}  # diff -> taller height
        for r in rods:
            cur = dp.copy()
            for diff, taller in cur.items():
                # Option 1: put rod on taller side
                nd = diff + r
                nt = taller + r
                if nd not in dp or dp[nd] < nt:
                    dp[nd] = nt

                # Option 2: put rod on shorter side
                nd = abs(diff - r)
                nt = taller + max(0, r - diff)
                if nd not in dp or dp[nd] < nt:
                    dp[nd] = nt

                # Option 3 (skip) is implicitly handled by keeping existing dp
        return dp.get(0, 0)

