class Solution:
    def isMatch(self, s: str, p: str) -> bool:
        i, j = 0, 0
        star_idx = -1
        last_match = 0
        n, m = len(s), len(p)

        while i < n:
            if j < m and (p[j] == s[i] or p[j] == '?'):
                i += 1
                j += 1
            elif j < m and p[j] == '*':
                star_idx = j
                last_match = i
                j += 1
            elif star_idx != -1:
                j = star_idx + 1
                last_match += 1
                i = last_match
            else:
                return False

        while j < m and p[j] == '*':
            j += 1

        return j == m

