from typing import List

class Solution:
    def canVisitAllRooms(self, rooms: List[List[int]]) -> bool:
        # Use a set to keep track of visited rooms
        visited = set()

        # Use a stack to manage the rooms we have keys to and need to visit
        stack = [0]  # Start with room 0

        # While there are rooms in the stack to visit
        while stack:
            # Pop the last room from the stack
            room = stack.pop()

            # If the room has not been visited yet
            if room not in visited:
                # Mark the room as visited
                visited.add(room)

                # Get all keys from this room, and push unvisited room keys to the stack
                for key in rooms[room]:
                    if key not in visited:
                        stack.append(key)

        # Return true if we have visited all rooms
        return len(visited) == len(rooms)
