class Solution:
    def combinationSum3(self, k: int, n: int) -> List[List[int]]:
        def backtrack(start, path, k, n):
            # Base case: If we have select all k numbers and they sum up to n
            if k == 0 and n == 0:
                result.append(path)
                return

            # Loop from start to 9, as we need only numbers between 1 to 9
            for i in range(start, 10):
                # If current number is greater than the remaining sum needed, break the loop
                if i > n:
                    break

                # Recursive call with current number included
                backtrack(i + 1, path + [i], k - 1, n - i)

        # Initialize result list
        result = []

        # Start backtracking with number 1 and an empty path
        backtrack(1, [], k, n)

        return result
