    def firstMissingPositive(self, nums: List[int]) -> int:
        n = len(nums)

        # First pass, mark numbers that are out of range with a special marker value equal to n + 1
        # and update the array, so any potential positive integer we want to find (1 to n) does not get affected
        for i in range(n):
            if nums[i] <= 0 or nums[i] > n:
                nums[i] = n + 1

        # Second pass, use the index as a hash key and the sign of the number as presence detector
