from typing import List

class Solution:
    def maxSubarraySumCircular(self, nums: List[int]) -> int:
        def kadane(array: List[int]) -> int:
            max_ending_here = max_so_far = array[0]
            for x in array[1:]:
                max_ending_here = max(x, max_ending_here + x)
                max_so_far = max(max_so_far, max_ending_here)
            return max_so_far

        total_sum = sum(nums)

        max_normal = kadane(nums)

        # Handling the circular subarray case
        max_wraparound = total_sum + kadane([-x for x in nums[1:-1]])

        # Edge case: all elements are negative
        # In this scenario, kadane on inverted nums gives 0 or a minimum valid circular empty subarray, 
        # the circular sum is invalid, as picking none should not be allowed, hence max_normal serves instead
        if max_wraparound == 0:
            return max_normal

        return max(max_normal, max_wraparound)
