class Solution:
    def partitionLabels(self, s: str) -> List[int]:
        # Store the last occurrence of each character in the string.
        last_occurrence = {char: idx for idx, char in enumerate(s)}
        partitions_sizes = []
        start = 0
        end = 0

        # Iterate over the string to determine partitions.
        for idx, char in enumerate(s):
            # Update the `end` to the furthest last occurrence of the current character.
            end = max(end, last_occurrence[char])
            # If the current index matches the `end`, we found a partition.
            if idx == end:
                # Calculate the size of the current partition and add it to the list.
                partitions_sizes.append(end - start + 1)
                # Move the start to the next index for the next partition
                start = idx + 1

        return partitions_sizes
