class Solution:
    def simplifyPath(self, path: str) -> str:
        # Stack to keep track of directories
        stack = []

        # Split the input path by '/' and process each part
        parts = path.split('/')

        for part in parts:
            # If the part is '..', pop from stack if not empty (move up one directory)
            if part == '..':
                if stack:
                    stack.pop()
            # Ignore current directory reference '.' or empty parts from multiple slashes
            elif part == '' or part == '.':
                continue
            # If it's a valid directory name, push onto stack
            else:
                stack.append(part)

        # Join the stack together with '/' for the canonical path
        # Prepend '/' for absolute path, and ensure no trailing '/'
        return '/' + '/'.join(stack)
