from collections import deque
from typing import List

class Solution:
    def ladderLength(self, beginWord: str, endWord: str, wordList: List[str]) -> int:
        # Ensure endWord is in the wordList
        if endWord not in wordList:
            return 0

        wordSet = set(wordList)  # Convert list to set for O(1) lookups
        queue = deque([(beginWord, 1)])  # Queue for BFS, stores (current_word, current_length)

        while queue:
            current_word, current_length = queue.popleft()

            # Generate all possible transformations of current_word
            for i in range(len(current_word)):
                for char in 'abcdefghijklmnopqrstuvwxyz':
                    # Change one character at a time
                    next_word = current_word[:i] + char + current_word[i+1:]

                    # If the next word transformation is the endWord, return the length of the path
                    if next_word == endWord:
                        return current_length + 1

                    # If the transformed word is in the wordList, add it to the queue and remove from the set
                    if next_word in wordSet:
                        wordSet.remove(next_word)
                        queue.append((next_word, current_length + 1))

        return 0  # Return 0 if no transformation sequence found
