from typing import List

class Solution:
    def increasingTriplet(self, nums: List[int]) -> bool:
        # Initialize two variables to track the first and second number of the potential triplet
        first = second = float('inf')

        # Iterate through each number in the list
        for num in nums:
            if num <= first:
                # Update first if the current number is smaller or equal to first
                first = num
            elif num <= second:
                # Update second if the current number is in between first and second
                second = num
            else:
                # If the current number is greater than both first and second, return True
                # Since we have found a triplet (first, second, num)
                return True

        # If we finished the entire list without finding such a triplet, return False
        return False
```

