class Solution:
    def eraseOverlapIntervals(self, intervals: List[List[int]]) -> int:
        # Step 1: Check for the empty input
        if not intervals:
            return 0

        # Step 2: Sort the intervals based on their end times
        intervals.sort(key=lambda x: x[1])

        # Step 3: Initialize variables
        count = 0  # Number of intervals to remove
        end = intervals[0][1]  # End time of the last added interval

        # Step 4: Iterate through the intervals
        for i in range(1, len(intervals)):
            # Step 5: Check if current interval overlaps with the last added interval
            if intervals[i][0] < end:
                # Increment count for overlapping interval
                count += 1
            else:
                # Update the end to the end of the current non-overlapping interval
                end = intervals[i][1]

        # Step 6: Return the count of intervals to remove
        return count
