from typing import List

class Solution:
    def compress(self, chars: List[str]) -> int:
        n = len(chars)
        if n == 0:
            return 0

        write_index = 0
        i = 0

        while i < n:
            char = chars[i]
            count = 0
            while i < n and chars[i] == char:
                i += 1
                count += 1

            chars[write_index] = char
            write_index += 1

            if count > 1:
                for digit in str(count):
                    chars[write_index] = digit
                    write_index += 1

        return write_index

