        # Step 1: Check lengths, if not equal return False
        if len(s) != len(t):
            return False

        # Step 2: Initialize a dictionary to count occurrences
        count = {}

        # Step 3: Count characters in `s`
        for char in s:
            if char in count:
                count[char] += 1
            else:
                count[char] = 1

        # Step 4: Subtract counts using characters in `t`
        for char in t:
            if char in count:
                count[char] -= 1
            else:
                count[char] = -1

        # Step 5: Check if all counts are zero
        for value in count.values():
            if value != 0:
                return False

        return True

