class Solution:
    def asteroidCollision(self, asteroids: List[int]) -> List[int]:
        # Stack to handle asteroid collisions
        stack = []

        # Iterate through each asteroid
        for asteroid in asteroids:
            # Use a loop to handle continuous collisions
            while stack and asteroid < 0 < stack[-1]:
                # Compare sizes for collision handling
                if stack[-1] < -asteroid:
                    stack.pop()  # The asteroid moving left is larger, so pop from stack and continue evaluating
