class Solution:
    def longestCommonPrefix(self, strs: List[str]) -> str:
        if not strs:
            return ""

        min_length = min(len(s) for s in strs)
        if min_length == 0:
            return ""

        prefix = strs[0][:min_length]

        for i in range(min_length):
            current_char = prefix[i]
            for s in strs:
                if s[i] != current_char:
                    return prefix[:i]

        return prefix
