class Solution:
    def longestCommonPrefix(self, strs: List[str]) -> str:
        # Edge case: if the input list is empty, return an empty string
        if not strs:
            return ""

        # Start with the prefix as the first string in the array
        prefix = strs[0]

        # Iterate through the rest of the strings in the array
        for s in strs[1:]:
            # Reduce the prefix until it matches the start of current string
            while not s.startswith(prefix):
                # Trim the prefix by removing the last character
                prefix = prefix[:-1]
                # If the prefix becomes empty, return empty string immediately
                if prefix == "":
                    return ""

        # Return the found prefix after checking all strings
        return prefix
