class Solution:
    def longestCommonSubsequence(self, text1: str, text2: str) -> int:
        # Determine the length of each string
        m, n = len(text1), len(text2)

        # Create a 2D DP array where dp[i][j] represents the length of LCS 
        # of text1[0:i] and text2[0:j]
        dp = [[0] * (n + 1) for _ in range(m + 1)]

        # Fill the DP table
        for i in range(1, m + 1):
            for j in range(1, n + 1):
                if text1[i - 1] == text2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1] + 1  # Characters match, increment LCS length found so far
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])  # Take max of excluding character from either text1 or text2

        # The answer is the length of the longest common subsequence of text1 and text2
        return dp[m][n]
