from typing import List
from bisect import bisect_left

class Solution:
    def lengthOfLIS(self, nums: List[int]) -> int:
        if not nums:
            return 0

        # Tail array will store the smallest tail of all increasing subsequences
        # having length i+1 in tail[i].
        tail = []

        for num in nums:
            pos = bisect_left(tail, num)  # Find the insertion point

            # If num is larger than any element in tail
            if pos == len(tail):
                tail.append(num)
            else:
                tail[pos] = num

        return len(tail)
